// import logo from './logo.svg';
// import { Helmet } from "helmet";
import { useRef } from "react";
import { Link } from "react-router-dom";
import './App.css';


function Login() {
    const lst = document.getElementById('list')   
    function handleSubmit(e){
        e.preventDefault()
        
    const email = useRef()
    const password = useRef()
        fetch("http://127.0.0.1:5000/api/loginDoctor",{
            method:"POST",
            headers: {
                'Content-Type': "application/json"
            },
            body: JSON.stringify({
                
                'email': email,
                'password': pass,
    
            })
            
        }).then(function(response){
            console.log({
             
                'email': email,
                'password': pass,
    
            });
            return response.json()
        }).then(function(data){
            console.log(data)
            if (data.message == "login successful")
            {
                if(lst.value == "doc"){
                    //open the request 
                    fetch("http://127.0.0.1:5000/api/Doctors")
                    .then(function(res) {
                        return res.json(); //return the JSON Promise
                    })
                    .then(function(posts) {
                        //iterate over each post [100 posts]
                    
                        posts.forEach(function(post) {
                            if(email == post.email && pass == post.password){
                                
                                window.open("doc.html")
                            }
                        })
                    })                
                }
            }
        })
    
    ///////////////////////////////////////////////////////////////////////////////////
    
        fetch("http://127.0.0.1:5000/api/loginPatient",{
            method:"POST",
            headers: {
                'Content-Type': "application/json"
            },
            body: JSON.stringify({
               
                'email': email,
                'password': pass,
    
            })
            
        }).then(function(response){
            console.log({
                
                'email': email,
                'password': pass,
    
            });
            return response.json()
        }).then(function(data){
            console.log(data)
            if (data.message == "login successful")
            {
                if(lst.value == "pnt"){
                    //open the request 
                    fetch("http://127.0.0.1:5000/api/Patients")
                    .then(function(res) {
                        return res.json(); //return the JSON Promise
                    })
                    .then(function(posts) {
                        //iterate over each post [100 posts]
                    
                        posts.forEach(function(post) {
                            if(email == post.email && pass == post.password){
                                window.open("patient.html?id="+ post.PatientId)
                            }
                        })
                    })                
                }
            }
        })
    
        /////////////////////////////////////////////////////////////////////
        fetch("http://127.0.0.1:5000/api/LoginLaboratorist",{
            method:"POST",
            headers: {
                'Content-Type': "application/json"
            },
            body: JSON.stringify({
               
                'email': email,
                'password': pass,
    
            })
            
        }).then(function(response){
            console.log({
               
                'email': email,
                'password': pass,
    
    });
            return response.json()
        }).then(function(data){
            console.log(data)
            if (data.message == "login successful")
            {
                if(lst.value == "lab"){
                    //open the request 
                    fetch("http://127.0.0.1:5000/api/Laboratorists")
                    .then(function(res) {
                        return res.json(); //return the JSON Promise
                    })
                    .then(function(posts) {
                        //iterate over each post [100 posts]
                    
                        posts.forEach(function(post) {
                            if(email == post.email && pass == post.password){
                                window.open("lab.html")
                            }
                        })
                    })                
                }
            }
        })
        //////////////////////////////////////////////////////////////////////////////////
    
        fetch("http://127.0.0.1:5000/api/loginAdmin",{
            method:"POST",
            headers: {
                'Content-Type': "application/json"
            },
            body: JSON.stringify({
               
                'email': email,
                'password': pass,
    
            })
            
        }).then(function(response){
            console.log({
               
                'email': email,
                'password': pass,
    
            });
            return response.json()
        }).then(function(data){
            console.log(data)
            if (data.message == "login successful")
            {
                if(lst.value == "pharma"){
                    //open the request 
                    fetch("http://127.0.0.1:5000/api/Admins")
                    .then(function(res) {
                        return res.json(); //return the JSON Promise
                    })
                    .then(function(posts) {
                        //iterate over each post [100 posts]
                    
                        posts.forEach(function(post) {
                            if(email == post.email && pass == post.password){
                                window.open("admin/index.html")
                            }
                        })
                    })                
                }
            }
        })
    
    



}

    return (
    <>
      <div>
        <form>
            <div class="form-group">
                <label for="cars">email:</label>
                <input ref={email} class="form-control" type="text" id= "email" placeholder="email"/>
            </div>
            <div class="form-group">
                <label for="cars">Password:</label>
                <input ref={password} class="form-control" type="password" id= "pass" placeholder="password"/>
            </div>
            <div class="form-group">
                
                <label for="cars">who are you:</label>
                <select id="list" >
                    <option id="doc" value="doc">Doctor</option>
                    <option id="patient" value="pnt">Patient</option>
                    <option id="lab" value="lab">Laboratorist</option>
                    <option id="pharma" value="pharma">Admin</option>
                </select>
            </div>
            <div class="form-group">           
                <input onClick={handleSubmit} type="submit" value="Login" class="btn btn-primary"/>
            </div>
        </form>
       </div>



    </>
    );
}
   
    
export default Login;
